const { createSlice } = require('@reduxjs/toolkit');

const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    items: [],
    totalQuantity: 0,
    totalAmount: 0
  },
  reducers: {
   addItemToCart(state,action){},
   removeItemFromCart(state,action){},

  }
});

export const uiActions = cartSlice.actions;

export default cartSlice;
